import React, { Component } from 'react';
import './very.easy.component.css';
import {connect} from 'react-redux';
import {createStore} from 'redux';
import {Provider} from 'react-redux';
import reducerOne from '../reducers/reducer';

const store=createStore(reducerOne);

class VeryEasyComponent extends Component {
  
  constructor(props){
    super(props);
    this.state={
      options:["dose","fare","hart","floe","feat"],
      
      guess:"",
      arrayoption:"options",
    
      status:"",
      matching:"",
    
      answer:"floe",
      answer2:"moan",
      number:"",
      tries:"4"
      
    }
    this.handleInput=this.handleInput.bind(this);
    this.handleSubmit=this.handleSubmit.bind(this);
    this.handleTries=this.handleTries.bind(this);
    this.handleNext=this.handleNext.bind(this);
    this.handlePrevious=this.handlePrevious.bind(this);
    this.handleRetry=this.handleRetry.bind(this);
   
   
  }
  handleRetry=()=>{
    this.setState({
      tries:"4",
      status:"",
      guess:"",
      matching:"",
      number:""
    })
  }
  handleNext=()=>{
    this.setState({
      tries:"4",
      status:"",
      guess:"",
      matching:"",
      number:"",
      options:["mown","moan","hour","pear","pale"]
    })
  }
  handlePrevious=()=>{
    this.setState({
      tries:"4",
      status:"",
      guess:"",
      matching:"",
      number:"",
      options:["dose","fare","hart","floe","feat"]
    })
  }
   handleTries=()=>{
     
     
      this.setState({
        tries:this.state.tries -1
      })
     
     
     

    
     

   }
  handleInput(e){
    
    this.setState({
      guess:e.target.value
    })
  }
  handleSubmit= e =>{
    
    e.preventDefault();
    
    // const newGuess = this.state.guess
    this.setState({
      matching:this.state.guess
    })
    if(this.state.answer===this.state.guess){
      this.setState({
        number:this.props.number1,
        status:"You Win"
        
      })
      
      
    }
    if(this.state.guess==="fare"){
      this.setState({
       number:this.props.number2,
       status:"retry"
      })
  }
  if(this.state.guess==="dose"){
    this.setState({
     number:this.props.number3,
     status:"retry"
    })
}
if(this.state.guess==="hart"){
  this.setState({
   number:this.props.number4,
   status:"retry"
  })
}
if(this.state.guess==="feat"){
  this.setState({
   number:this.props.number5,
   status:"retry"
  })
}




//conditions for array 2

if(this.state.answer2===this.state.guess){
  this.setState({
    number:this.props.number,
    status:"You Win"
    
  })
  
  
}
if(this.state.guess==="mown"){
  this.setState({
   number:this.props.number6,
   status:"retry"
  })
}
if(this.state.guess==="pale"){
this.setState({
 number:this.props.number7,
 status:"retry"
})
}
if(this.state.guess==="hour"){
this.setState({
number:this.props.number8,
status:"retry"
})
}
if(this.state.guess==="pear"){
this.setState({
number:this.props.number9,
status:"retry"
})
}


    // const newMatching=this.state.number2
    // const updtedMatchings=[...this.state.matchings,newMatching]
    // console.log(newMatching);
    // console.log(updtedMatchings);
    // this.setState({
    //   matchings:updtedMatchings,
    //   matching:""
    // })
   
    
    
    // const updatedGuesses=[...this.state.guesses,newGuess]
    // console.log(updatedGuesses);
    // this.setState({
    //   guesses:updatedGuesses,
    //   guess:""
    // })


  }
 
    render() {
    
        return (
            <Provider store={store}>
              
            <div className="  container-fluid">
                 <a className="home-icon" href="http://localhost:3000/">Home</a>
                <div classname="row">
                <div className="col-lg-2 col-md-2 col-sm-2 col-xs-2 col-2"></div>
                <div className="col-lg-8 col-md-8 col-sm-8 col-xs-8 col-8">
                    <div  class=" very-easy card">
                      <form onSubmit={this.handleSubmit} > 
                      <h4>Very Easy Level!</h4>
                      
              
              
                        <label style={{fontWeight:'bold'}}>input guess</label>
                        <input value={this.state.guess}
                         onChange={this.handleInput}
                         placeholder="type here..." className=" input form-control" type="text"  />
                        <input onClick={this.handleTries}  className="submit-button" type="Submit"/>
                        </form>
                        <ul>
        
        {this.state.options.map(item => (
            <li className="list" key={item}>{item}</li>
          ))}
                  </ul>        
                    <table class="">
    <thead style={{borderTop:'1px solid aqua'}}>
      <tr>
        <th scope="col">Guess</th>
        <th scope="col">Matching letters</th>
       
        
      </tr>
    </thead>
    <tbody style={{border:'1px solid aqua'}}>
      <tr style={{border:'1px solid aqua'}} >
        <td style={{border:'1px solid aqua'}}> 
        
        {this.state.matching}
        </td>
        
        <td>
          
          {this.state.number}
        </td>
       
       
      </tr>
      <tr>
        <td>
          <button onClick={this.handleRetry} className="btm">Retry</button>
          <button onClick={this.handleNext} className="btm">Next</button>
          <button onClick={this.handlePrevious} className="btm">Previous</button>
        <span className="status">{this.state.status}</span>
        <span> Tries Left={this.state.tries}</span>
        </td>
      </tr>
    </tbody>
  </table>
  
</div>
                </div>
                <div className="col-lg-2 col-md-2 col-sm-2 col-xs-2 col-2"></div>
                </div>
                
            </div>
            
            </Provider>
            
        );
    }
}

const mapStateToProps=(state)=>{
  return{
    number1:state.matching_number,
    number2:state.matching_number1,
    number3:state.matching_number2,
    number4:state.matching_number3,
    number5:state.matching_number4,
    // second level matchings
    number6:state.matching_number5,
    number7:state.matching_number6,
    number8:state.matching_number7,
    number9:state.matching_number8,

  }
}

export default connect(mapStateToProps)(VeryEasyComponent) ;
