import React, {Component} from 'react';

class HardComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            options: ["scorpian", "flogging", "croppers", "migraine", "footnote", "refinary", "vaulting", "vicarage", "protract", "descents"],
            guess: "",
            arrayoption: "options",
            status: "",
            matching: "",
            answer: "refinary",
            answer2: "vocation",
            number: "",
            tries: "4"
        }
        this.handleInput = this.handleInput.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleTries = this.handleTries.bind(this);
        this.handleNext = this.handleNext.bind(this);
        this.handlePrevious = this.handlePrevious.bind(this);
        this.handleRetry = this.handleRetry.bind(this);


    }

    handleRetry = () => {
        this.setState({
            tries: "4",
            status: "",
            guess: "",
            matching: "",
            number: ""
        })
    }
    handleNext = () => {
        this.setState({
            tries: "4",
            status: "",
            guess: "",
            matching: "",
            number: "",
            options: ["economic", "enviable", "felicity", "diseased", "desirous", "vocation", "incident", "accident", "allusion", "illusion"],
        })
    }
    handlePrevious = () => {
        this.setState({
            tries: "4",
            status: "",
            guess: "",
            matching: "",
            number: "",
            options: ["scorpian", "flogging", "croppers", "migraine", "footnote", "refinary", "vaulting", "vicarage", "protract", "descents"],
        })
    }
    handleTries = () => {


        this.setState({
            tries: this.state.tries - 1
        })


    }

    handleInput(e) {

        this.setState({
            guess: e.target.value
        })
    }

    handleSubmit = e => {

        e.preventDefault();

        // const newGuess = this.state.guess
        this.setState({
            matching: this.state.guess
        })
        if (this.state.answer === this.state.guess) {
            this.setState({
                number: "8/8",
                status: "You Win"

            })


        }
        if (this.state.guess === "scorpian") {
            this.setState({
                number: "4/8",
                status: "retry"
            })
        }
        if (this.state.guess === "flogging") {
            this.setState({
                number: "2/8",
                status: "retry"
            })
        }
        if (this.state.guess === "croppers") {
            this.setState({
                number: "2/8",
                status: "retry"
            })
        }
        if (this.state.guess === "migraine") {
            this.setState({
                number: "5/8",
                status: "retry"
            })
        }
        if (this.state.guess === "footnote") {
            this.setState({
                number: "2/8",
                status: "retry"
            })
        }
        if (this.state.guess === "vicarage") {
            this.setState({
                number: "3/8",
                status: "retry"
            })
        }
        if (this.state.guess === "vaulting") {
            this.setState({
                number: "2/8",
                status: "retry"
            })
        }

        if (this.state.guess === "protract") {
            this.setState({
                number: "2/8",
                status: "retry"
            })
        }

        if (this.state.guess === "descents") {
            this.setState({
                number: "2/8",
                status: "retry"
            })
        }
//conditions for array 2

        if (this.state.answer2 === this.state.guess) {
            this.setState({
                number: "8/8",
                status: "You Win"

            })


        }
        if (this.state.guess === "economic") {
            this.setState({
                number: "4/8",
                status: "retry"
            })
        }
        if (this.state.guess === "enviable") {
            this.setState({
                number: "3/8",
                status: "retry"
            })
        }
        if (this.state.guess === "felicity") {
            this.setState({
                number: "3/8",
                status: "retry"
            })
        }
        if (this.state.guess === "diseased") {
            this.setState({
                number: "2/8",
                status: "retry"
            })
        }
        if (this.state.guess === "desirous") {
            this.setState({
                number: "2/8",
                status: "retry"
            })
        }
        if (this.state.guess === "incident") {
            this.setState({
                number: "4/8",
                status: "retry"
            })
        }
        if (this.state.guess === "accident") {
            this.setState({
                number: "5/8",
                status: "retry"
            })
        }
        if (this.state.guess === "allusion") {
            this.setState({
                number: "4/8",
                status: "retry"
            })
        }
        if (this.state.guess === "illussion") {
            this.setState({
                number: "4/8",
                status: "retry"
            })
        }
        // const newMatching=this.state.number2
        // const updtedMatchings=[...this.state.matchings,newMatching]
        // console.log(newMatching);
        // console.log(updtedMatchings);
        // this.setState({
        //   matchings:updtedMatchings,
        //   matching:""
        // })


        // const updatedGuesses=[...this.state.guesses,newGuess]
        // console.log(updatedGuesses);
        // this.setState({
        //   guesses:updatedGuesses,
        //   guess:""
        // })


    }

    render() {

        return (


            <div className="  container-fluid">
                <a className="home-icon" href="http://localhost:3000/">Home</a>
                <div classname="row">
                    <div className="col-lg-2 col-md-2 col-sm-2 col-xs-2 col-2"></div>
                    <div className="col-lg-8 col-md-8 col-sm-8 col-xs-8 col-8">
                        <div class=" very-easy card">
                            <form onSubmit={this.handleSubmit}>
                                <h4>Hard Level!</h4>


                                <label style={{fontWeight: 'bold'}}>input guess</label>
                                <input value={this.state.guess}
                                       onChange={this.handleInput}
                                       placeholder="type here..." className=" input form-control" type="text"/>
                                <input onClick={this.handleTries} className="submit-button" type="Submit"/>
                            </form>
                            <ul>

                                {this.state.options.map(item => (
                                    <li className="list" key={item}>{item}</li>
                                ))}
                            </ul>
                            <table class="">
                                <thead style={{borderTop: '1px solid aqua'}}>
                                <tr>
                                    <th scope="col">Guess</th>
                                    <th scope="col">Matching letters</th>


                                </tr>
                                </thead>
                                <tbody style={{border: '1px solid aqua'}}>
                                <tr style={{border: '1px solid aqua'}}>
                                    <td style={{border: '1px solid aqua'}}>

                                        {this.state.matching}
                                    </td>

                                    <td>

                                        {this.state.number}
                                    </td>


                                </tr>
                                <tr>
                                    <td>
                                        <button onClick={this.handleRetry} className="btm">Retry</button>
                                        <button onClick={this.handleNext} className="btm">Next</button>
                                        <button onClick={this.handlePrevious} className="btm">Previous</button>
                                        <span className="status">{this.state.status}</span>
                                        <span> Tries Left={this.state.tries}</span>
                                    </td>
                                </tr>
                                </tbody>
                            </table>

                        </div>
                    </div>
                    <div className="col-lg-2 col-md-2 col-sm-2 col-xs-2 col-2"></div>
                </div>

            </div>


        );
    }
}

export default HardComponent;
